mapper.select:
SELECT id, title, description, start_time AS startTime, end_time AS endTime, image_url AS image_url, create_time AS createTime, status FROM donation_event
AND (title LIKE CONCAT('%', #{params.keyword}, '%') OR description LIKE CONCAT('%', #{params.keyword}, '%'))
AND status = #{params.status}
ORDER BY createTime DESC
LIMIT #{params.offset}, #{params.limit}

SELECT COUNT(1) FROM donation_event
AND (title LIKE CONCAT('%', #{params.keyword}, '%') OR description LIKE CONCAT('%', #{params.keyword}, '%'))
AND status = #{params.status}

SELECT COUNT(DISTINCT donor_id) AS exchangeCount
FROM donation_item
WHERE event_id = #{eventId}
