mapper.select:
SELECT id, item_id, reviewer_id, review_status, review_comment, review_time
FROM donation_review
WHERE id = #{id}

SELECT id, item_id, reviewer_id, review_status, review_comment, review_time
FROM donation_review

     AND item_id = #{params.itemId}
     AND reviewer_id = #{params.reviewerId}

ORDER BY review_time DESC
